   222‚Üíasync function promptForKey(keyName, keyConfig) {
   223‚Üí  return new Promise((resolve) => {
   224‚Üí    console.log(`\nüìã ${keyConfig.description}`);
   225‚Üí    
   226‚Üí    if (keyConfig.instructions) {
   227‚Üí      console.log(`‚ÑπÔ∏è  Instructions: ${keyConfig.instructions}`);
   228‚Üí    }
   229‚Üí    
   230‚Üí    if (keyConfig.example) {
   231‚Üí      console.log(`üí° Example: ${keyConfig.sensitive ? '[HIDDEN]' : keyConfig.example}`);
   232‚Üí    }
   233‚Üí    
   234‚Üí    if (keyConfig.default) {
   235‚Üí      console.log(`‚ö° Default: ${keyConfig.default}`);
   236‚Üí    }
   237‚Üí    
   238‚Üí    if (keyConfig.generate) {
   239‚Üí      console.log(`üîÑ Can be auto-generated if left empty`);
   240‚Üí    }
   241‚Üí    
   242‚Üí    const requiredText = keyConfig.required ? ' (REQUIRED)' : ' (Optional)';
   243‚Üí    const prompt = `Enter ${keyName}${requiredText}: `;
   244‚Üí    
   245‚Üí    rl.question(prompt, (answer) => {
   246‚Üí      // Handle defaults and auto-generation
   247‚Üí      if (!answer && keyConfig.default) {
   248‚Üí        answer = keyConfig.default;
   249‚Üí      }
   250‚Üí      
   251‚Üí      if (!answer && keyConfig.generate) {
   252‚Üí        answer = keyConfig.generate();
   253‚Üí        console.log(`‚úÖ Auto-generated secure value`);
   254‚Üí      }
   255‚Üí      
   256‚Üí      // Validation
   257‚Üí      if (keyConfig.required && !answer) {
   258‚Üí        console.log(`‚ùå ${keyName} is required. Please provide a value.`);
   259‚Üí        return promptForKey(keyName, keyConfig).then(resolve);
   260‚Üí      }
   261‚Üí      
   262‚Üí      if (answer && keyConfig.validate && !keyConfig.validate(answer)) {
   263‚Üí        console.log(`‚ùå Invalid format for ${keyName}. Please check the example.`);
   264‚Üí        return promptForKey(keyName, keyConfig).then(resolve);
   265‚Üí      }
   266‚Üí      
   267‚Üí      if (answer) {
   268‚Üí        console.log(`‚úÖ ${keyName} configured`);
   269‚Üí      } else {
   270‚Üí        console.log(`‚è≠Ô∏è  Skipped ${keyName} (optional)`);
   271‚Üí      }
   272‚Üí      
   273‚Üí      resolve(answer);
   274‚Üí    });
   275‚Üí  });
   276‚Üí}
