   308‚Üí  fs.writeFileSync(path.join(process.cwd(), 'frontend', '.env.local'), frontendEnv);
   309‚Üí  
   310‚Üí  // Create example files
   311‚Üí  fs.writeFileSync(path.join(process.cwd(), 'backend', '.env.example'), 
   312‚Üí    Object.keys(requiredKeys).map(key => `${key}=`).join('\n'));
