   399‚Üíasync function main() {
   400‚Üí  try {
   401‚Üí    // Create directories if they don't exist
   402‚Üí    ['backend', 'frontend'].forEach(dir => {
   403‚Üí      if (!fs.existsSync(dir)) {
   404‚Üí        fs.mkdirSync(dir, { recursive: true });
   405‚Üí      }
   406‚Üí    });
   407‚Üí    
   408‚Üí    // Collect all configuration
   409‚Üí    const config = await collectAllKeys();
   410‚Üí    
   411‚Üí    // Generate environment files
   412‚Üí    generateEnvFiles(config);
   413‚Üí    
   414‚Üí    // Validate configuration
   415‚Üí    const issues = validateConfiguration(config);
   416‚Üí    
   417‚Üí    // Display summary
   418‚Üí    displaySummary(config, issues);
   419‚Üí    
   420‚Üí    // Save configuration summary
   421‚Üí    fs.writeFileSync('lif3-config-summary.json', JSON.stringify({
   422‚Üí      timestamp: new Date().toISOString(),
   423‚Üí      config: Object.keys(config).reduce((acc, key) => {
   424‚Üí        acc[key] = config[key] ? (requiredKeys[key]?.sensitive ? '[CONFIGURED]' : '‚úÖ') : '‚ùå';
   425‚Üí        return acc;
   426‚Üí      }, {}),
   427‚Üí      issues
   428‚Üí    }, null, 2));
   429‚Üí    
   430‚Üí    console.log('\nüíæ Configuration saved to: lif3-config-summary.json');
   431‚Üí    
   432‚Üí  } catch (error) {
