  'GOOGLE_CLIENT_SECRET': {
    description: 'Google OAuth2 Client Secret',
    required: true,
    example: 'GOCSPX-xxxxxxxxxxxxxxxxx',
    instructions: 'Get from: https://console.cloud.google.com/ > Credentials'
