  // Investment Platform APIs (Optional)
  'EASY_EQUITIES_API_KEY': {
    description: 'Easy Equities API Key (Optional)',
    required: false,
    example: 'ee_live_xxxxxxxxxxxxx'
  },

  // Cryptocurrency APIs (Optional)
  'LUNO_API_KEY': {
