    instructions: 'Get from: https://railway.app/account/tokens'
  },

  'VERCEL_TOKEN': {
    description: 'Vercel Deployment Token (Optional)',
    required: false,
    example: 'vercel_xxxxxxxxxxxxx',
    instructions: 'Get from: https://vercel.com/account/tokens'
