    },
    userId: savedUser.id,
    notes: 'Day 1 - Foundation building phase for 43V3R AI business',
  });

  const savedBusinessMetrics = await businessMetricsRepository.save(businessMetrics);
  console.log('üìä Created 43V3R business metrics starting from R0');

  // Create initial net worth snapshot
  const netWorthSnapshot = netWorthSnapshotRepository.create({
    netWorth: 0,
    liquidCash: 0,
    investments: 0,
    businessEquity: 0,
    totalAssets: 0,
    totalLiabilities: 0,
    targetNetWorth: 1800000,
    progressPercentage: 0,
    currency: Currency.ZAR,
    breakdown: {
      accounts: savedAccounts.map(account => ({
        accountId: account.id,
        accountName: account.name,
        balance: 0,
      })),
      investments: [],
      business: {
        dailyRevenue: 0,
        mrr: 0,
        valuation: 0,
      },
    },
    userId: savedUser.id,
    notes: 'Fresh start baseline - beginning the journey from R0 to R1,800,000',
  });

  const savedSnapshot = await netWorthSnapshotRepository.save(netWorthSnapshot);
  console.log('üìà Created initial net worth snapshot: R0 baseline');

  console.log('‚úÖ Fresh Start Database Seeding Complete!');
  console.log('üéØ Ready to track journey from R0 ‚Üí R1,800,000');
  console.log('üöÄ 43V3R business tracking initialized');
  console.log('üìä All automation systems ready for fresh start');

  return {
    user: savedUser,
    accounts: savedAccounts,
    goals: savedGoals,
    businessMetrics: savedBusinessMetrics,
    netWorthSnapshot: savedSnapshot,
