   570‚Üí  private parseFinancialInsight(responseText: string, type: string): FinancialInsight {
   571‚Üí    // This is a simplified parser - in production, you'd use more sophisticated parsing
   572‚Üí    return {
   573‚Üí      type,
   574‚Üí      title: `${type.replace('_', ' ')} Analysis`,
   575‚Üí      summary: responseText.substring(0, 200) + '...',
   576‚Üí      recommendations: this.extractRecommendations(responseText),
   577‚Üí      riskLevel: this.extractRiskLevel(responseText),
   578‚Üí      confidence: 0.85,
   579‚Üí      actionItems: this.extractActionItems(responseText),
   580‚Üí      timestamp: new Date()
   581‚Üí    };
