   583‚Üí
   584‚Üí  private extractRecommendations(text: string): string[] {
   585‚Üí    // Extract numbered recommendations from the response
   586‚Üí    const recommendations = text.match(/\d+\.\s+([^\n]+)/g) || [];
   587‚Üí    return recommendations.slice(0, 5);
   588‚Üí  }
   589‚Üí
   590‚Üí  private extractRiskLevel(text: string): 'LOW' | 'MEDIUM' | 'HIGH' {
   591‚Üí    if (text.toLowerCase().includes('high risk')) return 'HIGH';
   592‚Üí    if (text.toLowerCase().includes('medium risk')) return 'MEDIUM';
   593‚Üí    return 'LOW';
   594‚Üí  }
   595‚Üí
   596‚Üí  private extractActionItems(text: string): string[] {
   597‚Üí    // Extract action items from the response
   598‚Üí    const actionItems = text.match(/[-‚Ä¢]\s+([^\n]+)/g) || [];
   599‚Üí    return actionItems.slice(0, 3);
   600‚Üí  }
