        include: ['documents', 'metadatas', 'distances']
      });

      // Process results
      const searchResults: SearchResult[] = [];

      if (results.documents && results.documents[0]) {
        for (let i = 0; i < results.documents[0].length; i++) {
          const distance = results.distances?.[0]?.[i] || 1;
          const similarity = 1 - distance; // Convert distance to similarity

          if (similarity >= (options.threshold || 0.7)) {
            const chunk: DocumentChunk = {
              id: results.ids?.[0]?.[i] || '',
              content: results.documents[0][i] || '',
              metadata: results.metadatas?.[0]?.[i] as DocumentMetadata || {} as DocumentMetadata
            };

            searchResults.push({
              chunk,
              similarity,
              relevance: this.calculateRelevance(options.query, chunk.content, similarity)
            });
          }
        }
      }

      // Sort by relevance
      searchResults.sort((a, b) => b.relevance - a.relevance);

      const duration = Date.now() - startTime;

      this.loggerService.logIntegration({
        service: 'RAG_CHROMADB',
        action: 'SEMANTIC_SEARCH',
        status: 'SUCCESS',
        duration,
        recordsProcessed: searchResults.length,
        timestamp: new Date(),
        metadata: {
          query: options.query,
          resultsFound: searchResults.length,
          threshold: options.threshold || 0.7,
          filters: options.filters
        }
      });

      return searchResults;

    } catch (error) {
      const duration = Date.now() - startTime;

      this.loggerService.logIntegration({
        service: 'RAG_CHROMADB',
        action: 'SEMANTIC_SEARCH',
        status: 'FAILED',
        duration,
        errorMessage: error.message,
        timestamp: new Date(),
        metadata: {
          query: options.query
        }
      });

      throw error;
    }
  }

  private async createDocumentChunks(
    content: string,
