        });

        this.loggerService.logIntegration({
          service: 'RAG_CHROMADB',
          action: 'DELETE_DOCUMENT',
          status: 'SUCCESS',
          timestamp: new Date(),
          metadata: {
            documentId,
            chunksDeleted: results.ids.length
          }
        });

        return true;
      }

      return false;
    } catch (error) {
      this.loggerService.logIntegration({
        service: 'RAG_CHROMADB',
        action: 'DELETE_DOCUMENT',
        status: 'FAILED',
        errorMessage: error.message,
        timestamp: new Date(),
        metadata: { documentId }
      });

      throw error;
    }
  }
}

