        continue;
      }

      const chunk: DocumentChunk = {
        id: crypto.randomUUID(),
        content: cleanChunkText,
        metadata: {
          ...metadata,
          chunkIndex,
          totalChunks: 0, // Will be updated after processing
          tokenCount: chunkTokens.length
        }
      };

      chunks.push(chunk);
      chunkIndex++;

