   237‚Üíasync function promptForKey(keyName, keyConfig) {
   238‚Üí  return new Promise((resolve) => {
   239‚Üí    console.log(`\\nüìã ${keyConfig.description}`);
   240‚Üí    
   241‚Üí    if (keyConfig.instructions) {
   242‚Üí      console.log(`‚ÑπÔ∏è  Instructions: ${keyConfig.instructions}`);
   243‚Üí    }
   244‚Üí    
   245‚Üí    if (keyConfig.example) {
   246‚Üí      console.log(`üí° Example: ${keyConfig.sensitive ? '[HIDDEN]' : keyConfig.example}`);
   247‚Üí    }
   248‚Üí    
   249‚Üí    if (keyConfig.default) {
   250‚Üí      console.log(`‚ö° Default: ${keyConfig.default}`);
   251‚Üí    }
   252‚Üí    
   253‚Üí    if (keyConfig.generate) {
   254‚Üí      console.log(`üîÑ Can be auto-generated if left empty`);
   255‚Üí    }
   256‚Üí    
   257‚Üí    const requiredText = keyConfig.required ? ' (REQUIRED)' : ' (Optional)';
   258‚Üí    const prompt = `Enter ${keyName}${requiredText}: `;
   259‚Üí    
   260‚Üí    rl.question(prompt, (answer) => {
   261‚Üí      // Handle defaults and auto-generation
   262‚Üí      if (!answer && keyConfig.default) {
   263‚Üí        answer = keyConfig.default;
   264‚Üí      }
   265‚Üí      
   266‚Üí      if (!answer && keyConfig.generate) {
   267‚Üí        answer = keyConfig.generate();
   268‚Üí        console.log(`‚úÖ Auto-generated secure value`);
   269‚Üí      }
   270‚Üí      
   271‚Üí      // Validation
   272‚Üí      if (keyConfig.required && !answer) {
   273‚Üí        console.log(`‚ùå ${keyName} is required. Please provide a value.`);
   274‚Üí        return promptForKey(keyName, keyConfig).then(resolve);
   275‚Üí      }
   276‚Üí      
   277‚Üí      if (answer && keyConfig.validate && !keyConfig.validate(answer)) {
   278‚Üí        console.log(`‚ùå Invalid format for ${keyName}. Please check the example.`);
   279‚Üí        return promptForKey(keyName, keyConfig).then(resolve);
   280‚Üí      }
   281‚Üí      
   282‚Üí      if (answer) {
   283‚Üí        console.log(`‚úÖ ${keyName} configured`);
   284‚Üí      } else {
   285‚Üí        console.log(`‚è≠Ô∏è  Skipped ${keyName} (optional)`);
   286‚Üí      }
   287‚Üí      
   288‚Üí      resolve(answer);
   289‚Üí    });
   290‚Üí  });
   291‚Üí}
