    26‚Üí
    27‚Üí  async generateRAGResponse(queryDto: RAGQueryDto): Promise<RAGResponse> {
    28‚Üí    const startTime = Date.now();
    29‚Üí
    30‚Üí    try {
    31‚Üí      // 1. Perform semantic search to get relevant context
