import React, { useState, useEffect, useRef } from 'react'
import {
  Card,
  CardContent,
  Typography,
  TextField,
  IconButton,
  Box,
  Chip,
  CircularProgress,
  Divider,
  Button,
  Tooltip
} from '@mui/material'
import {
  Send,
  Psychology,
  AutoAwesome,
  TrendingUp,
  Business,
  AccountBalance,
  History,
  Clear
} from '@mui/icons-material'
import axios from 'axios'

interface Message {
  id: string
  type: 'user' | 'assistant'
  content: string
  timestamp: Date
  metadata?: {
    confidence?: number
    sources?: string[]
    analysis_type?: string
  }
}

interface ClaudeAIAssistantProps {
  currentMetrics?: {
    netWorth: number
    dailyRevenue: number
    progress: number
  }
}

const ClaudeAIAssistant: React.FC<ClaudeAIAssistantProps> = ({ currentMetrics }) => {
  const [messages, setMessages] = useState<Message[]>([])
  const [inputValue, setInputValue] = useState('')
  const [isLoading, setIsLoading] = useState(false)
  const [isConnected, setIsConnected] = useState(false)
  const messagesEndRef = useRef<HTMLDivElement>(null)

  // Quick suggestion prompts
  const quickPrompts = [
    "What's my current financial situation?",
    "How can I reach R1.8M faster?",
    "Best investment strategies for me?",
    "43V3R business growth advice",
    "Risk assessment for my portfolio"
  ]

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' })
  }

  useEffect(() => {
    scrollToBottom()
  }, [messages])

  useEffect(() => {
    // Check if Claude CLI integration is available
    checkConnection()
  }, [])

  const checkConnection = async () => {
    try {
      const response = await axios.get('/api/rag/stats')
      setIsConnected(true)

      // Add welcome message
      if (messages.length === 0) {
        addWelcomeMessage()
      }
    } catch (error) {
      setIsConnected(false)
      console.warn('Claude AI integration not available:', error)
    }
  }

  const addWelcomeMessage = () => {
    const welcomeMessage: Message = {
      id: `welcome-${Date.now()}`,
      type: 'assistant',
      content: `üëã Hi! I'm your LIF3 AI Financial Assistant powered by Claude CLI + RAG.

I can help you with:
‚Ä¢ Financial planning and investment strategies
‚Ä¢ 43V3R business growth advice  
‚Ä¢ Progress analysis toward your R1.8M goal
‚Ä¢ Risk assessment and portfolio optimization
‚Ä¢ South African market insights

Current Status:
üí∞ Net Worth: R${currentMetrics?.netWorth?.toLocaleString() || '239,625'}
üéØ Progress: ${currentMetrics?.progress?.toFixed(1) || '13.3'}% toward R1.8M
üöÄ 43V3R Revenue: R${currentMetrics?.dailyRevenue?.toLocaleString() || '0'}/day

Ask me anything about your financial journey!`,
      timestamp: new Date(),
      metadata: {
        analysis_type: 'welcome',
        confidence: 1.0
      }
    }
    setMessages([welcomeMessage])
  }

  const handleSendMessage = async (message?: string) => {
    const queryText = message || inputValue.trim()
    if (!queryText || isLoading) return

    const userMessage: Message = {
      id: `user-${Date.now()}`,
      type: 'user',
      content: queryText,
      timestamp: new Date()
    }

    setMessages(prev => [...prev, userMessage])
    setInputValue('')
    setIsLoading(true)

    try {
      // Send query to RAG backend first
      const ragResponse = await axios.post('/api/rag/query', {
        query: queryText,
        maxContextTokens: 4000,
        contextChunks: 5,
        includeMetrics: true,
        userContext: {
          netWorth: currentMetrics?.netWorth || 239625,
          progress: currentMetrics?.progress || 13.3,
          dailyRevenue: currentMetrics?.dailyRevenue || 0,
          goal: 1800000
        }
      })

      const assistantMessage: Message = {
        id: `assistant-${Date.now()}`,
        type: 'assistant',
        content: ragResponse.data.response || 'I received your question but couldn\'t generate a response. Please try again.',
        timestamp: new Date(),
        metadata: {
          confidence: ragResponse.data.confidence || 0.8,
          sources: ragResponse.data.sources || [],
          analysis_type: ragResponse.data.analysisType || 'general'
        }
      }

      setMessages(prev => [...prev, assistantMessage])

    } catch (error) {
      console.error('AI Assistant error:', error)

      const errorMessage: Message = {
        id: `error-${Date.now()}`,
        type: 'assistant',
        content: '‚ùå I\'m having trouble connecting right now. Please ensure the backend is running and try again.',
        timestamp: new Date(),
        metadata: {
          confidence: 0,
          analysis_type: 'error'
        }
      }

      setMessages(prev => [...prev, errorMessage])
    } finally {
      setIsLoading(false)
    }
  }

  const handleKeyPress = (event: React.KeyboardEvent) => {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault()
      handleSendMessage()
    }
  }

  const clearConversation = () => {
    setMessages([])
    addWelcomeMessage()
  }

  const formatTimestamp = (date: Date) => {
    return date.toLocaleTimeString('en-ZA', { 
      hour: '2-digit', 
      minute: '2-digit' 
    })
  }

  return (
    <Card sx={{ height: '600px', display: 'flex', flexDirection: 'column' }}>
      <CardContent sx={{ pb: 1 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 2 }}>
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <Psychology sx={{ mr: 1, color: 'primary.main' }} />
            <Typography variant="h6">LIF3 AI Assistant</Typography>
            <Box sx={{ 
              ml: 2,
              width: 8, 
              height: 8, 
              borderRadius: '50%', 
              backgroundColor: isConnected ? 'success.main' : 'error.main'
            }} />
          </Box>
          <Tooltip title="Clear conversation">
            <IconButton size="small" onClick={clearConversation}>
              <Clear />
            </IconButton>
          </Tooltip>
        </Box>

        {/* Quick Actions */}
        <Box sx={{ mb: 2, display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
          {quickPrompts.slice(0, 3).map((prompt, index) => (
            <Chip
              key={index}
              label={prompt}
              size="small"
              variant="outlined"
              onClick={() => handleSendMessage(prompt)}
              sx={{ fontSize: '0.75rem' }}
            />
          ))}
        </Box>
      </CardContent>

      <Divider />

      {/* Messages Area */}
      <Box sx={{ 
        flex: 1, 
        overflow: 'auto', 
        p: 2, 
        display: 'flex', 
        flexDirection: 'column',
        gap: 2
      }}>
        {messages.map((message) => (
          <Box key={message.id}>
            <Box sx={{ 
              display: 'flex', 
              justifyContent: message.type === 'user' ? 'flex-end' : 'flex-start',
              mb: 1
            }}>
              <Box sx={{
                maxWidth: '80%',
                p: 2,
                borderRadius: 2,
                backgroundColor: message.type === 'user' 
                  ? 'primary.main' 
                  : 'grey.100',
                color: message.type === 'user' ? 'white' : 'text.primary'
              }}>
                <Typography variant="body2" sx={{ whiteSpace: 'pre-wrap' }}>
                  {message.content}
                </Typography>

                {/* Metadata for assistant messages */}
                {message.type === 'assistant' && message.metadata && (
                  <Box sx={{ mt: 1, pt: 1, borderTop: '1px solid rgba(0,0,0,0.1)' }}>
                    {message.metadata.confidence && (
                      <Typography variant="caption" color="text.secondary">
                        Confidence: {(message.metadata.confidence * 100).toFixed(0)}%
                      </Typography>
                    )}
                    {message.metadata.analysis_type && (
                      <Chip 
                        label={message.metadata.analysis_type} 
                        size="small" 
                        variant="outlined"
                        sx={{ ml: 1, height: 20, fontSize: '0.7rem' }}
                      />
                    )}
                  </Box>
                )}
              </Box>
            </Box>

            <Typography variant="caption" color="text.secondary" sx={{ 
              display: 'block',
              textAlign: message.type === 'user' ? 'right' : 'left',
              px: 1
            }}>
              {formatTimestamp(message.timestamp)}
            </Typography>
          </Box>
        ))}

        {isLoading && (
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <CircularProgress size={16} />
            <Typography variant="body2" color="text.secondary">
              AI Assistant is thinking...
            </Typography>
          </Box>
        )}

        <div ref={messagesEndRef} />
      </Box>

      <Divider />

      {/* Input Area */}
      <Box sx={{ p: 2 }}>
        <Box sx={{ display: 'flex', gap: 1 }}>
          <TextField
            fullWidth
            size="small"
            placeholder="Ask me about your finances, investments, or business strategy..."
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onKeyPress={handleKeyPress}
            disabled={isLoading || !isConnected}
            multiline
            maxRows={3}
            sx={{ 
              '& .MuiOutlinedInput-root': {
                borderRadius: 2
              }
            }}
          />
          <IconButton 
            color="primary" 
            onClick={() => handleSendMessage()}
            disabled={!inputValue.trim() || isLoading || !isConnected}
            sx={{ 
              bgcolor: 'primary.main',
              color: 'white',
              '&:hover': { bgcolor: 'primary.dark' },
              '&:disabled': { bgcolor: 'grey.300' }
            }}
          >
            <Send />
          </IconButton>
        </Box>

        {!isConnected && (
          <Typography variant="caption" color="error" sx={{ mt: 1, display: 'block' }}>
            AI Assistant offline - backend connection required
          </Typography>
        )}
      </Box>
    </Card>
  )
}

export default ClaudeAIAssistant

